#ifndef PUGL_SRC_MACROS_H
#define PUGL_SRC_MACROS_H

#ifndef MIN
#  define MIN(a, b) (((a) < (b)) ? (a) : (b))
#endif

#ifndef MAX
#  define MAX(a, b) (((a) > (b)) ? (a) : (b))
#endif

#endif // PUGL_SRC_MACROS_H
